import readcharclass Human:            def move(self):        while(1):            k=readchar.readkey()            if k!='':break        if k=='\x1b[A' or k=='\x00H':            return 0        elif k=='\x1b[B' or k=='\x00P':            return 1        elif k=='\x1b[C' or k=='\x00M':            return 3        elif k=='\x1b[D' or k=='\x00K':            return 2        elif k == 'q':            exit()        else:            return self.move()        